class Tooltip{constructor(e){this.element=e,this.message=e.getAttribute("data-message")}init(){const e=document.createElement("div");e.classList.add("tip"),e.textContent=this.message,this.element.appendChild(e),this.element.addEventListener("mouseenter",(()=>{e.classList.add("active")})),this.element.addEventListener("mouseleave",(()=>{e.classList.remove("active")}))}}const tooltip=new Tooltip(document.querySelector(".tooltip"));tooltip.init();